var suggestions=document.getElementById('suggestions'),userinput=document.getElementById('userinput');document.addEventListener('keydown',inputFocus);function inputFocus(a){a.keyCode===191&&(a.preventDefault(),userinput.focus()),a.keyCode===27&&(userinput.blur(),suggestions.classList.add('d-none'))}document.addEventListener('click',function(a){var b=suggestions.contains(a.target);b||suggestions.classList.add('d-none')}),document.addEventListener('keydown',suggestionFocus);function suggestionFocus(b){const d=suggestions.querySelectorAll('a'),e=[...d],a=e.indexOf(document.activeElement);let c=0;b.keyCode===38?(b.preventDefault(),c=a>0?a-1:0,d[c].focus()):b.keyCode===40&&(b.preventDefault(),c=a+1<e.length?a+1:a,d[c].focus())}(function(){var a=new FlexSearch.Document({tokenize:"forward",cache:100,document:{id:'id',store:["href","title","description"],index:["title","description","content"]}});a.add({id:0,href:"https://v2raya.org/en/docs/prologue/introduction/",title:"Introduction",description:"v2rayA 是一个易用而强大的，专注于 Linux 的 V2Ray 客户端。",content:""}).add({id:1,href:"https://v2raya.org/en/docs/prologue/installation/",title:"Installation",description:"Install v2ray-core and v2rayA",content:""}).add({id:2,href:"https://v2raya.org/en/docs/prologue/quick-start/",title:"Quick Start",description:"快速上手 v2rayA",content:'\u003cul\u003e\n\u003cli\u003e\u003ca href="#%E5%AE%89%E8%A3%85-v2ray-%E5%86%85%E6%A0%B8--xray-%E5%86%85%E6%A0%B8"\u003e安装 V2Ray 内核 / Xray 内核\u003c/a\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href="#arch-linux-%E5%8F%8A%E5%85%B6%E8%A1%8D%E7%94%9F%E7%89%88"\u003eArch Linux 以及它的衍生版\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#%E4%BD%BF%E7%94%A8-systemd-%E7%9A%84-linux-%E5%8F%91%E8%A1%8C%E7%89%88"\u003e使用 systemd 的 Linux 发行版\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#alpine-linux"\u003eAlpine Linux\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#openwrt"\u003eOpenWrt\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003c/li\u003e\n\u003cli\u003e\u003ca href="#%E5%AE%89%E8%A3%85-v2raya"\u003e安装 v2rayA\u003c/a\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href="#debian-%E7%B3%BB%E5%88%97%E5%AE%89%E8%A3%85"\u003eDebian 系列安装\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#redhatcentos--opensuse-%E7%B3%BB%E5%88%97%E5%AE%89%E8%A3%85"\u003eRedHat(CentOS) / openSUSE 系列安装\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#alpine-linux"\u003eAlpine Linux\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#arch-linux-%E4%BB%A5%E5%8F%8A%E5%AE%83%E7%9A%84%E8%A1%8D%E7%94%9F%E7%89%88-1"\u003eArch Linux 以及它的衍生版\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#OpenWrt"\u003eOpenWrt\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#docker-%E6%96%B9%E5%BC%8F"\u003eDocker 方式\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003c/li\u003e\n\u003cli\u003e\u003ca href="#%E5%AE%89%E8%A3%85%E5%AE%8C%E6%AF%95%E5%90%8E%E5%A6%82%E4%BD%95%E4%BD%BF%E7%94%A8"\u003e安装完毕后如何使用\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#%E4%BD%BF%E7%94%A8%E5%85%B6%E4%BB%96%E7%B1%BB-v2ray-%E6%A0%B8%E5%BF%83"\u003e使用其他类 V2Ray 核心\u003c/a\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href="#systemd-%E6%96%B9%E6%A1%88"\u003esystemd 方案\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#alpine-linux"\u003eAlpine Linux\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="#openwrt"\u003eOpenWrt\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003c/li\u003e\n\u003cli\u003e\u003ca href="#%E7%8E%AF%E5%A2%83%E5%8F%98%E9%87%8F"\u003e环境变量\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id="安装-v2ray-内核--xray-内核"\u003e安装 V2Ray 内核 / Xray 内核\u003c/h2\u003e\n\u003ch3 id="arch-linux-及其衍生版"\u003eArch Linux 及其衍生版\u003c/h3\u003e\n\u003cp\u003e从官方源安装 \u003ccode\u003ev2ray\u003c/code\u003e，或者从 AUR 安装 \u003ccode\u003exray\u003c/code\u003e 或 \u003ccode\u003exray-bin\u003c/code\u003e。\u003c/p\u003e\n\u003ch3 id="使用-systemd-的-linux-发行版"\u003e使用 systemd 的 Linux 发行版\u003c/h3\u003e\n\u003cp\u003eV2Ray 安装参考：\u003ca href="https://github.com/v2fly/fhs-install-v2ray"\u003ehttps://github.com/v2fly/fhs-install-v2ray\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003eXray 安装参考：\u003ca href="https://github.com/XTLS/Xray-install"\u003ehttps://github.com/XTLS/Xray-install\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003e安装后可以关掉服务，因为v2rayA不依赖于该systemd服务。\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003esudo systemctl disable v2ray --now ### Xray 需要替换服务为 xray\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch3 id="alpine-linux"\u003eAlpine Linux\u003c/h3\u003e\n\u003cp\u003eV2Ray 安装参考：\u003ca href="https://github.com/v2fly/alpinelinux-install-v2ray"\u003ehttps://github.com/v2fly/alpinelinux-install-v2ray\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003eXray 安装参考：\u003ca href="https://github.com/XTLS/alpinelinux-install-xray"\u003ehttps://github.com/XTLS/alpinelinux-install-xray\u003c/a\u003e\u003c/p\u003e\n\u003ch3 id="openwrt"\u003eOpenWrt\u003c/h3\u003e\n\u003cp\u003e首先安装软件包 \u003ccode\u003eunzip\u003c/code\u003e 与 \u003ccode\u003ewget\u003c/code\u003e，然后下载 v2ray 内核然后将其保存到 \u003ccode\u003e/usr/bin\u003c/code\u003e，下载链接为\u003ca href="https://github.com/v2fly/v2ray-core/releases"\u003ehttps://github.com/v2fly/v2ray-core/releases\u003c/a\u003e，最后给予二进制文件可执行权限。\u003c/p\u003e\n\u003cp\u003e例如：\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003eopkg update; opkg install unzip wget\nwget https://github.com/v2fly/v2ray-core/releases/download/v4.40.1/v2ray-linux-64.zip\nunzip -d v2ray-core v2ray-linux-64.zip\ncp v2ray-core/v2* /usr/bin; cp v2ray-core/*dat /usr/bin\nchmod +x /usr/bin/v2ray; chmod +x /usr/bin/v2ctl\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003e格外注意你的 OpenWrt 设备的架构，不要下载到不适用于你设备的版本，否则内核将无法运行。Xray 内核可参照此方法安装。\u003c/p\u003e\n\u003ch2 id="安装-v2raya"\u003e安装 v2rayA\u003c/h2\u003e\n\u003ch3 id="debian-系列安装"\u003eDebian 系列安装\u003c/h3\u003e\n\u003ch4 id="方法一通过软件源安装"\u003e方法一：通过软件源安装\u003c/h4\u003e\n\u003ch5 id="添加公钥"\u003e添加公钥\u003c/h5\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003ewget -qO - https://apt.v2raya.mzz.pub/key/public-key.asc | sudo apt-key add -\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch5 id="添加-v2raya-软件源"\u003e添加 V2RayA 软件源\u003c/h5\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003eecho \u0026quot;deb https://apt.v2raya.mzz.pub/ v2raya main\u0026quot; | sudo tee /etc/apt/sources.list.d/v2raya.list\nsudo apt update\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch5 id="安装-v2raya-1"\u003e安装 V2RayA\u003c/h5\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003esudo apt install v2raya\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="方法二手动安装-deb-包"\u003e方法二：手动安装 deb 包\u003c/h4\u003e\n\u003cp\u003e\u003ca href="https://github.com/v2rayA/v2rayA/releases"\u003e下载 deb 包\u003c/a\u003e后可以使用 Gdebi、QApt 等图形化工具来安装，也可以使用命令行：\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003esudo apt install /path/download/installer_debian_xxx_vxxx.deb ### 自行替换 deb 包所在的实际路径\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch3 id="redhatcentos--opensuse-系列安装"\u003eRedHat(CentOS) / openSUSE 系列安装\u003c/h3\u003e\n\u003cp\u003e\u003ca href="https://github.com/v2rayA/v2rayA/releases"\u003e下载 rpm 包\u003c/a\u003e后运行：\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003esudo rpm -i /path/download/installer_redhat_xxx_vxxx.rpm ### 自行替换 rpm 包所在的实际路径\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch3 id="alpine-linux-1"\u003eAlpine Linux\u003c/h3\u003e\n\u003ch4 id="1-下载二进制可执行文件"\u003e1. 下载二进制可执行文件\u003c/h4\u003e\n\u003cp\u003e根据你的平台，从 \u003ca href="https://github.com/v2rayA/v2rayA/releases"\u003eRelease\u003c/a\u003e 获取具有 \u003ccode\u003ev2raya_linux_xxx\u003c/code\u003e 字样的无后缀名文件，并将其重命名为 \u003ccode\u003ev2raya\u003c/code\u003e，再把 \u003ccode\u003ev2raya\u003c/code\u003e 移动到 \u003ccode\u003e/usr/local/bin\u003c/code\u003e 并给予可执行权限。\u003c/p\u003e\n\u003cp\u003e示例：\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003ewget https://github.com/v2rayA/v2rayA/releases/download/v1.4.1/v2raya_linux_amd64_v1.4.1 -O v2raya\nsmv ./v2raya /usr/local/bin/ \u0026amp;\u0026amp; chmod +x /usr/local/bin/v2raya\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="2-安装网页前端"\u003e2. 安装网页前端\u003c/h4\u003e\n\u003cp\u003e下载 v2rayA 的 Web 页面，然后保存到\u003ccode\u003e/usr/local/etc/v2raya/web\u003c/code\u003e。\u003c/p\u003e\n\u003cp\u003e示例：\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003ewget https://github.com/v2rayA/v2raya-web/archive/refs/heads/master.zip\nunzip master.zip\nmkdir -p /usr/local/etc/v2raya/web\nmv -r ./v2raya-web-master/* /usr/local/etc/v2raya/web\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="3-创建服务文件"\u003e3. 创建服务文件\u003c/h4\u003e\n\u003cp\u003e在 \u003ccode\u003e/etc/init.d/\u003c/code\u003e 目录下面新建一个名为 \u003ccode\u003ev2raya\u003c/code\u003e 的文本文件，然后编辑，添加内容如下：\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-ini"\u003e#!/sbin/openrc-run\n\nname=\u0026quot;v2rayA\u0026quot;\ndescription=\u0026quot;A Linux web GUI client of Project V which supports V2Ray, Xray, SS, SSR, Trojan and Pingtunnel\u0026quot;\ncommand=\u0026quot;/usr/local/bin/v2raya\u0026quot;\ncommand_args=\u0026quot;--webdir=/usr/local/etc/v2raya/web --config=/usr/local/etc/v2raya\u0026quot;\npidfile=\u0026quot;/run/${RC_SVCNAME}.pid\u0026quot;\ncommand_background=\u0026quot;yes\u0026quot;\n\ndepend() {\n need net\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003e保存文件，然后给予此文件可执行权限。\u003c/p\u003e\n\u003ch4 id="4-安装-iptables-模块"\u003e4. 安装 iptables 模块\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003eapk add iptables ip6tables\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="5-运行-v2raya-并开机启动"\u003e5. 运行 v2rayA 并开机启动\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003erc-service v2raya start\nrc-update add v2raya\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch3 id="arch-linux-以及它的衍生版"\u003eArch Linux 以及它的衍生版\u003c/h3\u003e\n\u003cp\u003e从 AUR 安装 \u003ccode\u003ev2raya\u003c/code\u003e 或 \u003ccode\u003ev2raya-bin\u003c/code\u003e、\u003ccode\u003ev2raya-git\u003c/code\u003e 即可。\u003c/p\u003e\n\u003ch3 id="openwrt-1"\u003eOpenWrt\u003c/h3\u003e\n\u003ch4 id="1-安装必须的软件包"\u003e1. 安装必须的软件包：\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003eopkg update\nopkg install ca-certificates tar curl\nopkg install kmod-ipt-nat6 iptables-mod-tproxy iptables-mod-filter\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="2-安装网页前端-1"\u003e2. 安装网页前端\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003ecd /tmp\nlatest_version=$(curl -s https://apt.v2raya.mzz.pub/dists/v2raya/main/binary-amd64/Packages|grep Version|cut -d\' \' -f2)\nwget https://apt.v2raya.mzz.pub/pool/main/v/v2raya/web_v${latest_version}.tar.gz\nmkdir /etc/v2raya\ntar xzvf web_v${latest_version}.tar.gz --directory /etc/v2raya\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="3-安装二进制可执行文件"\u003e3. 安装二进制可执行文件\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003ewget -O /usr/bin/v2raya https://apt.v2raya.mzz.pub/pool/main/v/v2raya/v2raya_linux_amd64_v${latest_version}\nchmod +x /usr/bin/v2raya\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="4-创建服务文件"\u003e4. 创建服务文件\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003enano /etc/init.d/v2raya\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003e内容如下：\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-ini"\u003e#!/bin/sh /etc/rc.common\ncommand=/usr/bin/v2raya\nPIDFILE=/var/run/v2raya.pid\ndepend() {\n  need net\n  after firewall\n  use dns logger\n}\nstart() {\n  start-stop-daemon -b -S -m -p \u0026quot;${PIDFILE}\u0026quot; -x $command\n}\nstop() {\n  start-stop-daemon -K -p \u0026quot;${PIDFILE}\u0026quot;\n}\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003e给予此文件可执行权限：\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003echmod +x /etc/init.d/v2raya\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="5-运行-v2raya-并开机启动-1"\u003e5. 运行 v2rayA 并开机启动\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003e/etc/init.d/v2raya start\n/etc/init.d/v2raya enable\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch3 id="docker-方式"\u003eDocker 方式\u003c/h3\u003e\n\u003cp\u003e使用 docker 命令部署。\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003e# run v2raya\ndocker run -d \\\n  --restart=always \\\n  --privileged \\\n  --network=host \\\n  --name v2raya \\\n  -e V2RAYA_ADDRESS=0.0.0.0:2017 \\\n      -v /lib/modules:/lib/modules \\\n  -v /etc/resolv.conf:/etc/resolv.conf \\\n  -v /etc/v2raya:/etc/v2raya \\\n  mzz2017/v2raya\n\u003c/code\u003e\u003c/pre\u003e\n\u003chr\u003e\n\u003cp\u003e如果你使用 MacOSX 或其他不支持 host 模式的环境，在该情况下\u003cstrong\u003e无法使用全局透明代理\u003c/strong\u003e，或者你不希望使用全局透明代理，docker 命令会略有不同：\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003e# run v2raya\ndocker run -d \\\n  -p 2017:2017 \\\n  -p 20170-20172:20170-20172 \\\n  --restart=always \\\n  --name v2raya \\\n  -v /etc/v2raya:/etc/v2raya \\\n  mzz2017/v2raya\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch2 id="v2raya-运行相关"\u003ev2rayA 运行相关\u003c/h2\u003e\n\u003cp\u003e如果 v2rayA 正常运行（启动或许需要一定时间）则开放 2017 作为管理端口，通过浏览器访问即可进行管理。如访问 http://localhost:2017/\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003e\n\u003cp\u003e导入并连接正常工作的节点后，设置全局透明代理即可使用。\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003e如果不使用全局透明代理，可使用浏览器插件如 SwitchyOmega 通过下述端口进行代理，或使用桌面环境提供的系统代理进行达到类似全局代理的效果。：\u003c/p\u003e\n\u003cp\u003e默认情况下开放三个代理端口：20170(socks5)、20171(http)、20172(带分流规则的 http)\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003e注意，如果通过 archlinuxcn 源安装，需要运行\u003ccode\u003esystemctl enable --now v2raya\u003c/code\u003e。\u003c/p\u003e\n\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id="使用其他类-v2ray-核心"\u003e使用其他类 V2Ray 核心\u003c/h2\u003e\n\u003cp\u003e一般而言 v2rayA 会优先选择 v2ray，其次是 xray。如需切换到 xray，卸载 v2ray 即可。\u003c/p\u003e\n\u003cp\u003e如果使用其他暂不主动支持的类 v2ray 核心，参考以下方法（以指定使用 Xray 为例）：\u003c/p\u003e\n\u003ch3 id="systemd-方案"\u003esystemd 方案\u003c/h3\u003e\n\u003col\u003e\n\u003cli\u003e\n\u003cp\u003e新建文件夹 \u003ccode\u003e/etc/systemd/system/v2raya.service.d\u003c/code\u003e，然后新建一个 \u003ccode\u003exray.conf\u003c/code\u003e 的文件，添加以下内容：\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-conf"\u003e[Service]\nEnvironment=\u0026quot;V2RAYA_V2RAY_BIN=/usr/local/bin/xray\u0026quot;\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003e注意检查 Xray 的路径是否正确。\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003e重载服务：\u003c/p\u003e\n\u003cpre\u003e\u003ccode\u003esudo systemctl daemon-reload \u0026amp;\u0026amp; sudo systemctl restart v2raya\n\u003c/code\u003e\u003c/pre\u003e\n\u003c/li\u003e\n\u003c/ol\u003e\n\u003ch3 id="alpine-linux-2"\u003eAlpine Linux\u003c/h3\u003e\n\u003cp\u003e修改 \u003ccode\u003e/etc/init.d/v2raya\u003c/code\u003e，然后在 \u003ccode\u003ecommand_args=\u0026quot;\u0026quot;\u003c/code\u003e 的内容里面加上 \u003ccode\u003e --v2ray-bin=/usr/local/bin/xray\u003c/code\u003e（注意\u0026ndash;v2ray-bin之前可能需要空格），再重启服务即可。\u003c/p\u003e\n\u003ch3 id="openwrt-2"\u003eOpenWrt\u003c/h3\u003e\n\u003cp\u003e（未经过测试）直接将 \u003ccode\u003ecommand\u003c/code\u003e 修改为 \u003ccode\u003e/usr/bin/v2raya --v2ray-bin=/usr/bin/xray\u003c/code\u003e 即可。\u003c/p\u003e\n\u003ch2 id="环境变量"\u003e环境变量\u003c/h2\u003e\n\u003cp\u003e\u003ccode\u003eV2RAYA_ADDRESS\u003c/code\u003e: 监听地址 (默认 \u0026ldquo;0.0.0.0:2017\u0026rdquo;)\u003c/p\u003e\n\u003cp\u003e\u003ccode\u003eV2RAYA_CONFIG\u003c/code\u003e: v2rayA 配置文件目录 (默认 \u0026ldquo;/etc/v2raya\u0026rdquo;)\u003c/p\u003e\n\u003cp\u003e\u003ccode\u003eV2RAYA_V2RAY_BIN\u003c/code\u003e: v2ray 可执行文件路径. 留空将自动检测. 可修改为 v2ray 分支如 xray 等文件路径\u003c/p\u003e\n\u003cp\u003e\u003ccode\u003eV2RAYA_V2RAY_CONFDIR\u003c/code\u003e: 附加的 v2ray 配置文件目录, 该目录中的 v2ray 配置文件会与 v2rayA 生成的配置文件进行组合\u003c/p\u003e\n\u003cp\u003e\u003ccode\u003eV2RAYA_WEBDIR\u003c/code\u003e: v2rayA 前端 GUI 文件目录 (默认 \u0026ldquo;/etc/v2raya/web\u0026rdquo;)\u003c/p\u003e\n\u003cp\u003e\u003ccode\u003eV2RAYA_PLUGINLISTENPORT\u003c/code\u003e: v2rayA 内部插件端口 (默认 32346)\u003c/p\u003e\n\u003cp\u003e\u003ccode\u003eV2RAYA_FORCE_IPV6_ON\u003c/code\u003e: 强制启用 IPv6，即使 v2rayA 认为该机器不支持 IPv6。\u003c/p\u003e\n\u003cp\u003e\u003ccode\u003eV2RAYA_PASSCHECKROOT\u003c/code\u003e: 跳过 root 权限检测, 确认你有 root 权限而 v2rayA 判断出错时使用\u003c/p\u003e\n\u003cp\u003e\u003ccode\u003eV2RAYA_VERBOSE\u003c/code\u003e: 详细日志模式，混合打印 v2ray-core 和 v2rayA 的运行日志\u003c/p\u003e\n\u003cp\u003e\u003ccode\u003eV2RAYA_RESET_PASSWORD\u003c/code\u003e: 重设密码\u003c/p\u003e\n'}).add({id:3,href:"https://v2raya.org/en/docs/help/how-to-update/",title:"How to Update",description:"Regularly update the installed npm packages to keep your Doks website stable, usable, and secure.",content:'\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003e💡 \u003c/div\u003e\n  \n    \u003cdiv class="w-100"\u003eLearn more about \u003ca href="https://docs.npmjs.com/about-semantic-versioning"\u003esemantic versioning\u003c/a\u003e and \u003ca href="https://docs.npmjs.com/cli/v6/using-npm/semver#advanced-range-syntax"\u003eadvanced range syntax\u003c/a\u003e. \u003c/div\u003e\n  \n\u003c/div\u003e\n\n\u003ch2 id="check-for-outdated-packages"\u003eCheck for outdated packages\u003c/h2\u003e\n\u003cp\u003eThe \u003ca href="https://docs.npmjs.com/cli/v7/commands/npm-outdated"\u003e\u003ccode\u003enpm outdated\u003c/code\u003e\u003c/a\u003e command will check the registry to see if any (or, specific) installed packages are currently outdated:\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003enpm outdated [[\u0026lt;@scope\u0026gt;/]\u0026lt;pkg\u0026gt; ...]\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch2 id="update-packages"\u003eUpdate packages\u003c/h2\u003e\n\u003cp\u003eThe \u003ca href="https://docs.npmjs.com/cli/v7/commands/npm-update"\u003e\u003ccode\u003enpm update\u003c/code\u003e\u003c/a\u003e command will update all the packages listed to the latest version (specified by the tag config), respecting semver:\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003enpm update [\u0026lt;pkg\u0026gt;...]\n\u003c/code\u003e\u003c/pre\u003e\n'}).add({id:4,href:"https://v2raya.org/en/docs/",title:"Docs",description:null,content:""}).add({id:5,href:"https://v2raya.org/en/docs/prologue/",title:"Get Start",description:"Get Start",content:""}),userinput.addEventListener('input',b,!0),suggestions.addEventListener('click',c,!0);function b(){var d,e;const c=5;d=this.value,e=a.search(d,{limit:c,enrich:!0}),suggestions.classList.remove('d-none'),suggestions.innerHTML="";const b={};e.forEach(a=>{a.result.forEach(a=>{b[a.doc.href]=a.doc})});for(const d in b){const e=b[d],a=document.createElement('div');if(a.innerHTML='<a href><span></span><span></span></a>',a.querySelector('a').href=d,a.querySelector('span:first-child').textContent=e.title,a.querySelector('span:nth-child(2)').textContent=e.description,suggestions.appendChild(a),suggestions.childElementCount==c)break}}function c(){while(suggestions.lastChild)suggestions.removeChild(suggestions.lastChild);return!1}})()